package com.syntaxclass7;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i =1;
		while(i<3) {
			System.out.println("hello");
		i++;
		}
			
		
		System.out.println("----DO WHILE-----");
		
		
		int a=1;
		
		do {
			System.out.println("HELLO");
			a++;
		} while(a<=3);
		
		
		System.out.println("---------------------");
		
		// print 1 to 10
		
		int n=1;
		do {
			System.out.println(n);
		n++;
		} while(n<=10);
		
	}

}
